import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import PatientDetail from './PatientDetail';
import './Dashboard.css';

const Dashboard = () => {
  const [darkMode, setDarkMode] = useState(localStorage.getItem('theme') === 'dark');
  const [currentTime, setCurrentTime] = useState(new Date());
  const [patients, setPatients] = useState([]);
  const hospitalName = "MediCare General Hospital";
  const doctorInfo = {
    name: "Dr. Sarah Johnson",
    specialty: "Cardiologist",
    id: "DOC-2023-45",
    avatar: "doctor-avatar.jpg"
  };

  // Toggle dark mode
  useEffect(() => {
    document.body.classList.toggle('dark-mode', darkMode);
    localStorage.setItem('theme', darkMode ? 'dark' : 'light');
  }, [darkMode]);

  // Update clock
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Fetch patients data
  useEffect(() => {
    // Simulating API call
    const fetchPatients = async () => {
      try {
        // In a real app, this would be an API call
        const mockPatients = [
          { id: 1, name: "John Doe", age: 45, status: "critical", room: "ICU-01" },
          { id: 2, name: "Jane Smith", age: 32, status: "needs attention", room: "Ward-B" },
          { id: 3, name: "Robert Brown", age: 67, status: "normal", room: "Ward-A" },
          { id: 4, name: "Emily Davis", age: 28, status: "critical", room: "ICU-03" },
          { id: 5, name: "Michael Wilson", age: 54, status: "normal", room: "Ward-C" }
        ];
        setPatients(mockPatients);
      } catch (error) {
        console.error("Error fetching patients:", error);
      }
    };

    fetchPatients();
  }, []);

  // Get status color
  const getStatusColor = (status) => {
    switch(status) {
      case 'critical': return 'status-critical';
      case 'needs attention': return 'status-attention';
      case 'normal': return 'status-normal';
      default: return '';
    }
  };

  // Format date for display
  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  return (
    <Router>
      <div className="dashboard-container">
        <aside className="sidebar">
          <div className="hospital-info">
            <h1>{hospitalName}</h1>
          </div>

          <div className="doctor-profile">
            <img src={doctorInfo.avatar} alt="Doctor" className="doctor-avatar" />
            <div className="doctor-details">
              <h2>{doctorInfo.name}</h2>
              <p>{doctorInfo.specialty}</p>
              <p>ID: {doctorInfo.id}</p>
            </div>
          </div>

          <div className="clock-widget">
            <div className="time">{currentTime.toLocaleTimeString()}</div>
            <div className="date">{formatDate(currentTime)}</div>
          </div>

          <div className="theme-toggle">
            <button onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
            </button>
          </div>
        </aside>

        <main className="main-content">
          <Switch>
            <Route exact path="/">
              <div className="dashboard-header">
                <h2>Patient Dashboard</h2>
              </div>

              <div className="patients-grid">
                {patients.map(patient => (
                  <Link to={`/patient/${patient.id}`} key={patient.id}>
                    <div className={`patient-card ${getStatusColor(patient.status)}`}>
                      <h3>{patient.name}</h3>
                      <p>Age: {patient.age}</p>
                      <p>Room: {patient.room}</p>
                      <p className="status">Status: {patient.status}</p>
                    </div>
                  </Link>
                ))}
              </div>
            </Route>

            <Route path="/patient/:id">
              <PatientDetail />
            </Route>
          </Switch>
        </main>
      </div>
    </Router>
  );
};

export default Dashboard;
