import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Chart from 'chart.js/auto';
import { MapContainer, TileLayer, Marker, Circle, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './PatientDetail.css';

// Fix for Leaflet marker icon issue in React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

const PatientDetail = () => {
  const { id } = useParams(); // This will get the patient ID from the URL

  // State for patient data
  const [patientData, setPatientData] = useState({
    name: "Loading...",
    sex: "Loading...",
    age: "--",
    bloodType: "--",
    bedNumber: "--",
    vitals: {
      bloodPressure: "--/--",
      pulse: "--",
      temperature: "--",
      spo2: "--",
      weight: "--",
      respiration: "--"
    },
    location: {
      lat: 28.6139,
      lng: 77.2090,
      accuracy: 15
    },
    ecgStatus: "Loading...",
    lastUpdated: "Loading..."
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [ecgChart, setEcgChart] = useState(null);
  const [locationUpdates, setLocationUpdates] = useState(true);

  useEffect(() => {
    // Function to fetch data from ThingSpeak
    const fetchThingSpeakData = async () => {
      try {
        const CHANNEL_ID = id; // Using the patient ID as the channel ID
        const READ_API_KEY = "MWFBV98HOZOHTHY4"; // Using your provided API key

        const url = `https://api.thingspeak.com/channels/${CHANNEL_ID}/feeds.json?api_key=${READ_API_KEY}&results=1`;

        const response = await fetch(url);

        if (!response.ok) {
          throw new Error(`Failed to fetch data: ${response.status}`);
        }

        const data = await response.json();

        if (data.feeds && data.feeds.length > 0) {
          const latestEntry = data.feeds[0];

          // Update patient data with ThingSpeak values
          // Assuming fields are mapped as follows:
          // field1: heart rate/pulse
          // field2: temperature
          // field3: spo2
          // field4: systolic BP
          // field5: diastolic BP
          // field6: latitude
          // field7: longitude
          // field8: respiration rate

          setPatientData(prevData => ({
            ...prevData,
            vitals: {
              bloodPressure: `${latestEntry.field4 || "--"}/${latestEntry.field5 || "--"}`,
              pulse: latestEntry.field1 || "--",
              temperature: latestEntry.field2 || "--",
              spo2: latestEntry.field3 || "--",
              weight: "80.0", // Default value as this might not be in ThingSpeak
              respiration: latestEntry.field8 || "--"
            },
            location: {
              lat: parseFloat(latestEntry.field6) || 28.6139,
              lng: parseFloat(latestEntry.field7) || 77.2090,
              accuracy: 15
            },
            ecgStatus: parseInt(latestEntry.field1) > 100 ? "Elevated" : "Normal",
            lastUpdated: new Date(latestEntry.created_at).toLocaleString()
          }));

          // Fetch additional patient information (in a real app, this would come from your backend)
          fetchPatientInfo();
        } else {
          throw new Error("No data found in ThingSpeak feed");
        }

        setLoading(false);
      } catch (err) {
        console.error("Error fetching ThingSpeak data:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    // Function to fetch additional patient info from your backend
    const fetchPatientInfo = async () => {
      try {
        // In a real application, you would fetch from your backend
        // For now, we'll use static data
        setPatientData(prevData => ({
          ...prevData,
          name: "Mr. Samarth Singla",
          sex: "Male",
          age: "32",
          bloodType: "B+",
          bedNumber: "0747"
        }));
      } catch (err) {
        console.error("Error fetching patient info:", err);
        // Keep existing data, don't update error state
      }
    };

    // Initial fetch
    fetchThingSpeakData();

    // Set up interval for periodic updates
    const interval = setInterval(() => {
      fetchThingSpeakData();
    }, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, [id]);

  // Initialize ECG chart
  useEffect(() => {
    if (loading) return;

    const ctx = document.getElementById('ecgChart');
    if (!ctx) return;

    // Cleanup previous chart if it exists
    if (ecgChart) {
      ecgChart.destroy();
    }

    // Generate sample ECG data (in a real app, this would come from ThingSpeak)
    const generateECGData = () => {
      const data = [];
      let last = 0;

      // Simulate ECG waveform
      for (let i = 0; i < 500; i++) {
        // Baseline
        let value = 0;

        // P wave
        if (i % 100 === 10) value = 0.5;
        if (i % 100 === 20) value = 0.3;

        // QRS complex
        if (i % 100 === 30) value = -1;
        if (i % 100 === 31) value = 2.5;
        if (i % 100 === 32) value = -1.5;
        if (i % 100 === 33) value = 0.5;

        // T wave
        if (i % 100 === 50) value = 0.8;
        if (i % 100 === 60) value = 0.4;

        // Add some noise
        value += (Math.random() - 0.5) * 0.2;

        // Smooth transitions
        value = last * 0.7 + value * 0.3;
        last = value;

        data.push(value);
      }

      return data;
    };

    const newChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: Array.from({length: 500}, (_, i) => i),
        datasets: [{
          label: 'ECG',
          data: generateECGData(),
          borderColor: '#00e676',
          borderWidth: 1,
          pointRadius: 0,
          tension: 0.1,
          fill: false
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          x: {
            display: false,
            grid: {
              display: false
            }
          },
          y: {
            display: false,
            min: -2,
            max: 3
          }
        },
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            enabled: false
          }
        },
        animation: {
          duration: 0
        }
      }
    });

    setEcgChart(newChart);

    // Simulate real-time ECG updates
    const updateECG = () => {
      if (newChart && newChart.data && newChart.data.datasets) {
        const data = newChart.data.datasets[0].data;
        data.shift();
        const newData = generateECGData();
        data.push(newData[0]);
        newChart.update();
      }
    };

    const ecgInterval = setInterval(updateECG, 50);
    return () => clearInterval(ecgInterval);
  }, [loading, ecgChart]);

  // Handle emergency alert
  const handleEmergencyAlert = () => {
    const { lat, lng } = patientData.location;
    alert(`Emergency alert sent! Patient location: ${lat.toFixed(4)}° N, ${lng.toFixed(4)}° E`);
    // In a real implementation, this would trigger an API call to emergency services
  };

  // Show loading or error state
  if (loading) {
    return <div className="loading">Loading patient data...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div className="dashboard">
      {/* Patient Header Section */}
      <div className="patient-header">
        <div className="patient-info">
          <h1>Patient</h1>
          <h2>{patientData.name}</h2>
          <div className="patient-details">
            <div className="detail-group">
              <div className="detail-label">SEX</div>
              <div className="detail-value">{patientData.sex}</div>
            </div>
            <div className="detail-group">
              <div className="detail-label">Age</div>
              <div className="detail-value">{patientData.age}</div>
            </div>
            <div className="detail-group">
              <div className="detail-label">Blood</div>
              <div className="detail-value">{patientData.bloodType}</div>
            </div>
            <div className="detail-group">
              <div className="detail-label">Bed #</div>
              <div className="detail-value">{patientData.bedNumber}</div>
            </div>
          </div>
        </div>
        <div className="patient-photo">
          <div className="initials">SS</div>
        </div>
      </div>

      {/* Vitals Grid Section */}
      <div className="vitals-container">
        <div className="section-title">Vital Signs</div>
        <div className="vitals-grid">
          {/* Blood Pressure */}
          <div className="vital-card">
            <div className="vital-label">Blood Pressure</div>
            <div className="vital-value bp">{patientData.vitals.bloodPressure}</div>
            <div className="vital-unit">mmHg</div>
          </div>

          {/* Pulse */}
          <div className="vital-card">
            <div className="vital-label">Pulse</div>
            <div className="vital-value pulse">{patientData.vitals.pulse}</div>
            <div className="vital-unit">bpm</div>
          </div>

          {/* Temperature */}
          <div className="vital-card">
            <div className="vital-label">Temperature</div>
            <div className="vital-value temp">{patientData.vitals.temperature}</div>
            <div className="vital-unit">°F</div>
          </div>

          {/* SPO2 */}
          <div className="vital-card">
            <div className="vital-label">SPO2</div>
            <div className="vital-value spo2">{patientData.vitals.spo2}</div>
            <div className="vital-unit">%</div>
          </div>

          {/* Weight */}
          <div className="vital-card">
            <div className="vital-label">Weight</div>
            <div className="vital-value">{patientData.vitals.weight}</div>
            <div className="vital-unit">kg</div>
          </div>

          {/* Respiration Rate */}
          <div className="vital-card">
            <div className="vital-label">Respiration</div>
            <div className="vital-value">{patientData.vitals.respiration}</div>
            <div className="vital-unit">breaths/min</div>
          </div>
        </div>
      </div>

      {/* Analysis Section */}
      <div className="analysis-section">
        <div className="analysis-title">Systolic Analysis</div>
        <div className="analysis-bars">
          <div className="bar-container">
            <div className="bar-label">20%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '20%' }}></div>
            </div>
            <div className="range-label">0-89</div>
          </div>
          <div className="bar-container">
            <div className="bar-label">35%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '35%' }}></div>
            </div>
            <div className="range-label">90-119</div>
          </div>
          <div className="bar-container">
            <div className="bar-label">53%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '53%' }}></div>
            </div>
            <div className="range-label">140-159</div>
          </div>
        </div>
      </div>

      <div className="analysis-section">
        <div className="analysis-title">Diastolic Analysis</div>
        <div className="analysis-bars">
          <div className="bar-container">
            <div className="bar-label">20%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '20%' }}></div>
            </div>
            <div className="range-label">0-89</div>
          </div>
          <div className="bar-container">
            <div className="bar-label">35%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '35%' }}></div>
            </div>
            <div className="range-label">90-119</div>
          </div>
          <div className="bar-container">
            <div className="bar-label">53%</div>
            <div className="bar">
              <div className="bar-fill" style={{ width: '53%' }}></div>
            </div>
            <div className="range-label">140-159</div>
          </div>
        </div>
      </div>

      {/* ECG Section */}
      <div className="ecg-section">
        <div className="ecg-title">ECG Monitoring</div>
        <div className="ecg-container">
          <div className="ecg-chart-container">
            <div className="chart-container">
              <canvas id="ecgChart"></canvas>
            </div>
          </div>
          <div className="ecg-summary">
            <div className="ecg-title">Summary</div>
            <div className="ecg-value">{patientData.ecgStatus}</div>
            <div className="ecg-meta">Heart Rate: {patientData.vitals.pulse} bpm</div>
            <div className="ecg-meta">Last Updated: {patientData.lastUpdated}</div>
            <div className="ecg-meta">Variability: 12 ms</div>
          </div>
        </div>
      </div>

      {/* GPS Tracking Section */}
      <div className="gps-section">
        <div className="gps-title">Patient Location Tracking</div>
        <div className="gps-container">
          <div className="gps-map-container">
            <MapContainer 
              center={[patientData.location.lat, patientData.location.lng]} 
              zoom={15} 
              style={{ height: '200px', width: '100%' }}
              id="map"
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              <Marker position={[patientData.location.lat, patientData.location.lng]}>
                <Popup>Patient Location</Popup>
              </Marker>
              <Circle 
                center={[patientData.location.lat, patientData.location.lng]}
                radius={patientData.location.accuracy}
                color="orange"
                fillColor="#ff6d00"
                fillOpacity={0.2}
              />
            </MapContainer>
          </div>
          <div className="gps-summary">
            <div className="gps-title">Location Details</div>
            <div className="gps-value" id="coordinates">
              {patientData.location.lat.toFixed(4)}° N, {patientData.location.lng.toFixed(4)}° E
            </div>
            <div className="gps-meta">Accuracy: {patientData.location.accuracy} meters</div>
            <div className="gps-meta">Last Update: {patientData.lastUpdated}</div>
            <div className="location-status">
              <div className={`status-indicator ${locationUpdates ? 'status-active' : 'status-inactive'}`}></div>
              <div className="gps-meta">GPS Signal: {locationUpdates ? 'Strong' : 'Weak'}</div>
            </div>
            <button className="emergency-button" onClick={handleEmergencyAlert}>
              EMERGENCY ALERT
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatientDetail;
